import React from 'react';
import { Route, Link } from 'react-router-dom';

const shortName = (fullname) => {
  const [name, surname] = fullname.split(' ');
  return `${name[0]}. ${surname}`;
};

const PostMenuLink = ({ id, to, label }) => (
  <Route path={`/blog/${id}`}>
    {({ match }) => (
      <Link to={to} className={match ? 'active' : ''}>{label}</Link>
    )}
  </Route>
);

export const PostMenu = ({ posts }) => (
  <nav className="posts-menu">
    {
      posts.map(post =>
        <PostMenuLink key={post.id} id={post.id} to={`/blog/${post.id}`} label={shortName(post.name)} />,
      )
    }
  </nav>
);

export default PostMenu;
